void Node::_updateBoundsIfNeeded() const {
  // Early-out if there's nothing to do
  if (!_hasDirtyBounds) {
    return;
  }

  struct Entry {
    ConstNodePtr node;
    int child;
  };

  // This function is not re-entrant, so assert if we ever try to re-enter
  static bool isUpdating;
  REPORT_IF_TRUE_DEBUG_ONLY(isUpdating);
  BoolScope scope(isUpdating, true);

  // Do a post-order traversal using a stack instead of recursion so we can't get a stack overflow
  // for deeply nested scene graphs.
  static Array<Entry> stack;
  stack.push_back(Entry{self(), 0});

  while (!stack.empty()) {
    Entry& top = stack.back();
    if (ConstNodePtr topNode = top.node) {
      // Make sure the children are up-to-date first
      if (top.child < topNode->childCount()) {
        if (auto child = topNode->childAtIndex(top.child++)) {
          if (child->_hasDirtyBounds) {
            stack.push_back(Entry{child, 0});
          }
        }
        continue;
      }

      // Once the children are up-to-date, update the parent
      REPORT_IF_FALSE_DEBUG_ONLY(topNode->_hasDirtyBounds);
      topNode->_updateSelfBoundsIfNeeded();
      REPORT_IF_TRUE_DEBUG_ONLY(topNode->_hasDirtyBounds);
    }
    stack.pop_back();
  }
}

void Node::_updateSelfBoundsIfNeeded() const {
  // We'll calculate geometry bounds differently in outline mode so that we can hit test things
  // that are not visible in normal rendering. We invalidate bounds on toggling in and out of
  // outline mode, so outline-specific bounds will be cached only for as long as we're in outline
  // mode.
  bool inOutlineMode = false;
  if (auto appState = getAppState_HackDontUse()) {
    inOutlineMode = appState->useOutlineMode();
  }

  // Early out if we're not connected to anything or invisible
  bool ignoreInvisible = opacityToRender() <= 0 && !inOutlineMode;
  if (!descendsFromRoot() || ignoreInvisible) {
    _cachedCanvasSpaceGeometryBounds = RectF::invalid();
    _cachedCanvasSpaceRenderedBounds = RectF::invalid();
    _hasDirtyBounds = false;
    return;
  }

  Rect geometryBounds = Rect::invalid();
  Rect renderedBounds = Rect::invalid();

  // Container node bounds include the bounds of their children.
  // Basically any node with children except vector operations, which has its own geometry cache.
  if (isNodeTypeInFrame(type()) || type() == NodeType::DOCUMENT || type() == NodeType::CANVAS) {
    for (const ConstNodePtr& constChild : children()) {
      NodePtr child = constChild.irresponsiblyCastAwayConst();
      if (child->opacityToRender() <= 0 && !inOutlineMode) {
        continue;
      }
      // Intersect this child's bounds by the bounds of the mask affecting this child if one
      // exists
      if (NodePtr mask = child->visibleSiblingMask()) {
        geometryBounds =
            geometryBounds.unionWith(Rect(child->_cachedCanvasSpaceGeometryBounds)
                                         .intersectWith(mask->_cachedCanvasSpaceGeometryBounds));
        renderedBounds =
            renderedBounds.unionWith(Rect(child->_cachedCanvasSpaceRenderedBounds)
                                         .intersectWith(mask->_cachedCanvasSpaceRenderedBounds));
      }

      // Masks themselves aren't visible
      else if (!child->isMask()) {
        geometryBounds = geometryBounds.unionWith(child->_cachedCanvasSpaceGeometryBounds);
        renderedBounds = renderedBounds.unionWith(child->_cachedCanvasSpaceRenderedBounds);
      }
    }

    // Frames that clip don't render anything outside their bounding box
    if (isUsingFrameClipping(self())) {
      auto absBounds = absoluteBounds();
      if (!inOutlineMode) {
        // In outline mode, nodes outside of clipping bounds are visible, so don't limit frame
        // bounds
        // -- otherwise we would prematurely cull children nodes in hit testing
        geometryBounds = geometryBounds.intersectWith(absBounds);
      }
      renderedBounds = renderedBounds.intersectWith(absBounds);
    }
  }

  // Include the node's own geometry. Note that frame-type nodes have their own geometry
  // in addition to the geometry of their children.
  if (usesGeometryCacheExcludingFrames(type()) ||
      (supportsStrokePaintData() && supportsFillPaintData())) {
    bool containsPaint = false;
    Immutable<PaintData> fills = fillPaintData();
    Immutable<PaintData> strokes = strokePaintData();

    // Pretend there aren't any strokes if the stroke weight is 0
    if (strokeWeight() <= 0) {
      strokes = Immutable<PaintData>();
    }

    // Check paints for visibility
    for (Immutable<PaintData> paintPtr : {fills, strokes}) {
      for (const auto& paint : paintPtr->paints()) {
        if (!paint->isVisibleIncludingGradientStops()) {
          continue;
        }
        containsPaint = true;
      }
    }

    // Only have geometry bounds if something will be drawn. Note that this deliberately doesn't
    // check style override tables for compatibility with "Node::hasPaintedFill()" and
    // "Node::hasPaintedStroke()".
    if (containsPaint) {
      if (type() == NodeType::TEXT) {
        RectF bounds = RectF::invalid();
        // Reuse one AffineTransform to avoid repeated allocations
        AffineTransform glyphTransform;
        for (size_t i = 0; i < derivedTextData()->laidGlyphs().size(); i++) {
          if (int(i) == derivedTextData()->truncationStartIndex().value_or(-1)) {
            break;
          }
          auto& glyph = derivedTextData()->laidGlyphs()[i];
          auto& pos = glyph.position;
          glyphTransform.reset();
          glyphTransform.translate(pos.x, round(pos.y));
          glyphTransform.scale(glyph.fontSize, -glyph.fontSize);
          RectF glyphBounds(glyphTransform.transformRect(glyph.glyph->path->bounds()));
          bounds.expandToInclude(glyphBounds);
        }

        for (auto& decoration : derivedTextData()->decorations()) {
          for (auto rect : decoration.rects) {
            bounds.expandToInclude(rect);
          }
        }

        Rect transformed = absoluteTransform().transformRect(bounds);
        geometryBounds = geometryBounds.unionWith(transformed);
      } else {
        geometryBounds = geometryBounds.unionWith(Node::geometryCache().transformedBounds());
      }
    }
  }

  if (inOutlineMode && type() == NodeType::VECTOR_OPERATION) {
    for (const ConstNodePtr& child : children()) {
      geometryBounds = geometryBounds.unionWith(child->canvasSpaceGeometryBounds());
    }
  }

  // Figure out the effect extent of this node.
  auto selfEffectExtent = Rect::invalid();
  const auto nodeEffectData = effectData();
  for (const auto& e : nodeEffectData->effects()) {
    // Ignore "INNER_SHADOW" and "BACKGROUND_BLUR" since those are clipped by "geometryBounds"
    if (e.type() == EffectType::DROP_SHADOW || e.type() == EffectType::FOREGROUND_BLUR) {
      selfEffectExtent = selfEffectExtent.unionWith(e.extent());
    }
  }

  // Make sure to include both the geometry bounds and the bounds of all effects on the node.
  // These two rectangles can be completely disjoint. For example, a drop shadow may have a very
  // large offset.
  renderedBounds = renderedBounds.unionWith(geometryBounds);

  // Make sure to allow zero-area rectangles, which represent an effect with a non-zero offset but
  // a zero blur radius. The offset is important to retain to ensure that effect bounds
  // computations are correct.
  renderedBounds = renderedBounds.unionWith(Effect::applyExtent(geometryBounds, selfEffectExtent));

  // Frames always need to include their bounding box in the two bounding boxes that we're
  // generating. We do this even if there are no children and also no frame background for are a
  // few reasons:
  //
  //   1) We need to render top-level frame names for empty frames and they wouldn't be rendered
  //   if we culled these frames as invisible.
  //
  //   2) We need to render frame outlines for empty frames in outline mode and they wouldn't be
  //   rendered if we culled these frames as invisible.
  //
  //   3) Our export bounds currently include all nested empty frame bounds. People may be using
  //   this functionality kind of like the slices feature.
  //
  // This has to be done after we "applyExtent" with "selfEffectExtent". We don't want the bounds
  // of frames with transparent backgrounds to be expanded by any drop shadows they have on them.
  if (isStackOrFixedSizeContainer()) {
    auto absBounds = absoluteBounds();
    geometryBounds = geometryBounds.unionWith(absBounds);
    renderedBounds = renderedBounds.unionWith(absBounds);
  }

  // The bounds should now be up to date
  _cachedCanvasSpaceGeometryBounds = RectF(geometryBounds);
  _cachedCanvasSpaceRenderedBounds = RectF(renderedBounds);

  _hasDirtyBounds = false;
}

void Node::invalidateCanvasSpaceBounds(Invalidate invalidate, NodeOrigin origin,
                                       bool invalidateRenderTree) {
  SceneGraph* scn = scene();
  if (!scn) {
    REPORT_ERROR("invalid canvas space bounds missing scene");
    return;
  }

  if (_scene->isLoading()) {
    // We shouldn't need to invalidate canvas space bounds during scene load
    // because this cache starts out dirty and should never be accessed until after the scene is
    // initialized.
    return;
  }

  for (NodePtr ancestor = self(); ancestor; ancestor = ancestor->parent()) {
    // Add flags to parents
    if (invalidate != Invalidate::CHILDREN) {
      ancestor->setBoundsDirty();
    }

    // Track a change counter per canvas so it's easy to detect when a canvas has changed
    if (ancestor->type() == NodeType::CANVAS) {
      int latestIndex = ++(scn->_renderChangeCounters[ancestor->uniqueID()].index);
      if (origin == NodeOrigin::LOCAL) {
        scn->_renderChangeCounters[ancestor->uniqueID()].indexOfLastLocalChange = latestIndex;
      }
    }
  }

  // Add flags to self and children
  if (invalidate != Invalidate::PARENTS) {
    forEachNode(self(), [](NodePtr node) {
      node->setBoundsDirty();
      // We don't have to deal with skipping defer-invisible-children here because
      // those nodes just won't exist!
      return Loop::CONTINUE;
    });
  }

  if (invalidateRenderTree) {
    invalidateRenderTreeForSelfAndParents();
  }
}

