bool Node::hasDiagramChildren() const {
  const Array<GUID>* diagramChildren = getDiagramChildren();
  return diagramChildren && !diagramChildren->empty();
}

const Array<GUID>* Node::getDiagramChildren() const {
  const auto& map = diagramChildrenForNode();
  auto it = map.find(_fields.dkey());
  return it != map.end() ? &it->second : nullptr;
}

NodePtr Node::diagramChildAtIndex(int index) const {
  const Array<GUID>* diagramChildren = getDiagramChildren();
  if (!diagramChildren || index < 0 || index >= static_cast<int>(diagramChildren->size())) {
    REPORT_ERROR("diagram child index out of range");
    return nullptr;
  }
  SceneGraph* scene = this->scene();
  return scene->nodeByIdOrNull(diagramChildren->at(index));
}

void Node::_diagramParentChanged(GUID oldParentId, GUID newParentId) {
  if (oldParentId != newParentId) {
    SceneGraph* scene = this->scene();
    if (not scene) return;
    GUID ourId = this->uniqueID();
    NodePtr newParent = scene->nodeByIdOrNull(newParentId);
    NodePtr oldParent = scene->nodeByIdOrNull(oldParentId);
    auto& map = diagramChildrenForNode();
    if (newParent) {
      // tell the parent that it has a new child.
      // note that we may try to add the same child twice in the case where we first
      // set the field (and call updateCachesAfterPropertyChange()), and then later add the
      // node to the scene (invoking changeParentIndex()).
      // We cannot avoid double-adding by deferring to call this method only to the time when we
      // eventually add the node to the scene, because we need to remove this node from its old
      // parent now, while we still know who the old parent was. Instead we check to see if the node
      // is already in the parent's list of children.
      auto it = map.find(newParent->_fields.dkey());
      if (it == map.end()) {
        it = map.insert_or_assign(newParent->_fields.dkey(), Array<GUID>()).first;
      }
      auto& kidsOfParent = it.value();
      auto i = std::find(kidsOfParent.begin(), kidsOfParent.end(), ourId);
      if (i == kidsOfParent.end()) kidsOfParent.push_back(ourId);
      // sort the layout children
      newParent->_diagramChildOrderChanged();
    }
    if (oldParent) {
      // tell the old parent it lost a child.
      auto it = map.find(oldParent->_fields.dkey());
      if (it == map.end()) return;
      auto& kidsOfOldParent = it.value();
      auto i = std::find(kidsOfOldParent.begin(), kidsOfOldParent.end(), ourId);
      if (i != kidsOfOldParent.end()) {
        kidsOfOldParent.erase(i);
      }
      if (kidsOfOldParent.size() == 0) {
        // if no more children, free the list
        map.erase(oldParent->_fields.dkey());
      }
    }
  }
}

void Node::_diagramChildOrderChanged() {
  auto& map = diagramChildrenForNode();
  auto it = map.find(_fields.dkey());
  if (it == map.end()) return;
  auto& kids = it.value();
  std::sort(kids.begin(), kids.end(), [&](const GUID& a, const GUID& b) {
    NodePtr nodeA = _scene->nodeByIdOrNull(a);
    NodePtr nodeB = _scene->nodeByIdOrNull(b);
    if (!nodeA || !nodeB) {
      return false;
    }

    const Position& positionA = nodeA->diagramParentIndex().position;
    const Position& positionB = nodeB->diagramParentIndex().position;
    if (positionA == positionB) {
      // Use the node ID as the tiebreaker
      return nodeA->uniqueID() < nodeB->uniqueID();
    } else {
      return positionA < positionB;
    }
  });
}

// If it has a diagramParentIndex or has a diagram layout rule type set, then it is a diagram
// node.
bool Node::isDiagramNode() const {
  return diagramParentIndex().isValid() || diagramLayoutRuleType() != DiagramLayoutRuleType::NONE;
}

// Set the diagram root node's layoutRule to the tree layout rule
void Node::setDiagramTreeLayoutRule() {
  NodePtr node = self();
  if (diagramParentIndex().isValid()) return;

  setDiagramLayoutRuleType(DiagramLayoutRuleType::TREE);
  node->updateDiagramLayoutRuleFromType();
}

Rect Node::computeBoundsForExport() const {
  Rect result(canvasSpaceRenderedBounds());

  if (isMask()) {
    // Masks don't have bounds themselves since they aren't visible. However, export bounds of
    // nodes nested inside masks need valid bounds for compatibility with BoundsHelper.
    return Rect::invalid();
  } else {
    // Slices don't have rendered bounds, but they do have export bounds (which are the same as
    // their absolute bounds).  Also, sometimes groups have invisible layers in them, which do not
    // affect the rendered bounds but should still be a part of the exported area.  These
    // invisible layers are accounted for in the absolute bounds, so we add the absolute bounds to
    // the result.
    if (type() == NodeType::SLICE || type() == NodeType::GROUP_DEPRECATED ||
        isNodeTypeInFrame(type())) {
      result = result.isInvalid() ? absoluteBounds() : result.unionWith(absoluteBounds());
    }
  }

  // Check the parents
  for (ConstNodePtr node = self(); node && node->type() != NodeType::CANVAS;
       node = node->parent()) {
    // Invisible nodes always have an empty export bound
    if (node->opacityToRender() <= 0) {
      return Rect::invalid();
    }

    // Deliberately return invalid bounds for nodes inside boolean ops
    if (node->type() == NodeType::VECTOR_OPERATION && node != self()) {
      return Rect::invalid();
    }

    // Clip bounds by parents
    if (node != self() && isUsingFrameClipping(node)) {
      result = result.intersectWith(node->absoluteBounds());
    }

    // Clip bounds by sibling masks
    if (auto mask = node->visibleSiblingMask()) {
      result = result.intersectWith(Rect(mask->canvasSpaceRenderedBounds()));
    }
  }

  return result;
}

// Sites in some cases needs the render bounds all nodes (even invisible nodes).
// "allowInvisibleNodes" tells this method to return bounds in those cases.
Rect Node::computeIsolatedBoundsForExport(bool allowInvisibleNodes) const {
  Rect result(canvasSpaceRenderedBounds());

  if (isMask() && !allowInvisibleNodes) {
    // Masks don't have bounds themselves since they aren't visible. However, export bounds of
    // nodes nested inside masks need valid bounds for compatibility with BoundsHelper.
    return Rect::invalid();
  } else {
    // Slices don't have rendered bounds, but they do have export bounds (which are the same as
    // their absolute bounds).  Also, sometimes groups have invisible layers in them, which do not
    // affect the rendered bounds but should still be a part of the exported area.  These
    // invisible layers are accounted for in the absolute bounds, so we add the absolute bounds to
    // the result.
    if (type() == NodeType::SLICE || type() == NodeType::GROUP_DEPRECATED ||
        isNodeTypeInFrame(type())) {
      result = result.isInvalid() ? absoluteBounds() : result.unionWith(absoluteBounds());
    }
  }

  // Invisible nodes always have an empty export bound
  if (opacityToRoot() <= 0 && !allowInvisibleNodes) {
    return Rect::invalid();
  }

  return result;
}

Rect Node::computeBoundsForExportOr32x32() const {
  auto result = computeBoundsForExport();

  // This is what the old BoundsHelper used to do
  if (result.isInvalid()) {
    return Rect(0, 0, 32, 32);
  }

  return result;
}

Rect Node::computeIsolatedBoundsForExportOr32x32() const {
  auto result = computeIsolatedBoundsForExport();

  // This is what the old BoundsHelper used to do
  if (result.isInvalid()) {
    return Rect(0, 0, 32, 32);
  }

  return result;
}

// Background blur render trees won't be invalidated during mutations. Our invalidations during
// mutations are very cheap and only walk up the parents. That works for everything except
// background blurs, where sibling nodes may contain blurred copies of our render tree.
//
// To handle this: whenever the canvas is mutated, we call this function to invalidate the render
// trees of all nodes with background blurs right before rendering a frame. To avoid this being
// really expensive if there are a lot of background blurs, we try to restrict this to only nodes
// that overlap the changes. However, for performance reasons we no longer track dirty rectangles
// as mutations happen so this isn't straightforward to do.
//
// As a hack, we diff the render trees of the previous frame with the current frame to approximate
// the area of the screen to check for background blurs to invalidate. This is a hack because
// render trees are not meant for this. For example, if we add an optimization that deletes render
// trees behind opaque fills, this will fail to generate any dirty rectangles and we will
// incorrectly fail to invalidate any nearby background blurs. Luckily we don't do that
// optimization yet so I think this hack is (probably?) correct. It's also a hack because the
// "current frame" isn't actually current (the background blurs are outdated because we haven't
// invalidated them yet). I think this (probably?) doesn't affect correctness because we currently
// disallow nested background blurs.
//
// This function returns true if the render tree was invalidated.
bool Node::invalidateNestedBackgroundBlurRenderTrees(const Rect& oldRect, const Rect& newRect) {
  auto oldTree = renderTree();
  if (!oldTree || !oldTree->hasBackgroundBlurs()) {
    return false;
  }

  bool childWasInvalidated = false;
  for (const auto child : children()) {
    childWasInvalidated |= child->invalidateNestedBackgroundBlurRenderTrees(oldRect, newRect);
  }

  // Invalidate now if a child was invalidated
  if (childWasInvalidated) {
    invalidateRenderTreeForSelf();
    return true;
  }

  auto t = type();
  bool hasGeometry = usesGeometryCacheExcludingFrames(t);

  // Stop now if this node can't have a background blur. Both nodes with geometry and frame-like
  // nodes support background blurs. Other node types (e.g. slices) don't support background
  // blurs.
  if (!hasGeometry && !isNodeTypeInFrame(t)) {
    return false;
  }

  // Check to see if there's a background blur
  {
    const auto& effects = effectData();

    // The background blur bounds for nodes with geometry is the same as the bounds of the
    // geometry bounds. The background blur bounds for frame-like nodes is the absolute bounds of
    // the node.
    auto bounds = hasGeometry && !isNodeTypeInFrame(t)
                      ? geometryCache().transformedBoundsApproximate()
                      : absoluteBounds();

    for (const auto& e : effects->effects()) {
      if (e.shouldRender() && e.type() == EffectType::BACKGROUND_BLUR) {
        auto expanded = bounds.expand(e.radius);

        // The background blur must be within the blur radius of the changed area
        if (oldRect.overlaps(expanded) || newRect.overlaps(expanded)) {
          invalidateRenderTreeForSelf();
          return true;
        }
      }
    }
  }

  return false;
}

void Node::invalidateRenderTreeForSelf() {
  if (!isValid()) return;

  _renderTree = nullptr;
  _hasRenderTree = false;
  if (_hasRenderTreeWithoutBackgroundBlurs) {
    renderTreeWithoutBackgroundBlursForNode().erase(_fields.dkey());
  }
  _hasRenderTreeWithoutBackgroundBlurs = false;
}

void Node::invalidateRenderTreeForSelfAndParents() {
  // Render tree hasn't been constructed yet during file load, there's no
  // reason to traverse the scene doing invalidation...
  if (_scene->isLoading()) {
    return;
  }

  for (NodePtr node = self(); node != nullptr; node = node->parent()) {
    node->invalidateRenderTreeForSelf();
    // If this invalidation is the result of an animateable property change from a
    // dynamic layer node, we should not propagate render tree invalidation to the root static
    // layer. We still need to invalidate our own render tree otherwise the node will be displayed
    // incorrectly when the node is moved back to the static layer at the end.
    if (node->shouldRenderOnIndependentLayer()) {
      break;
    }
  }
}

void Node::invalidateRenderTreeForSelfAndChildren() {
  invalidateRenderTreeForSelf();
  invalidateRenderTreeForChildren();
}

void Node::invalidateRenderTreeForChildren() {
  // Render tree hasn't been constructed yet during file load, there's no
  // reason to traverse the scene doing invalidation...
  if (_scene->isLoading()) {
    return;
  }

  for (const auto child : children()) {
    child->invalidateRenderTreeForSelf();
    child->invalidateRenderTreeForChildren();
  }
}

RenderTree::RenderNodePtr Node::renderTree() const {
  REPORT_IF_TRUE(_scene->isExpandingInstances());
  REPORT_IF_TRUE(_scene->isLoading());

  if (isValid() && !_hasRenderTree) {
    RenderTree::GenerateArgs args;
    if (auto appState = getAppState_HackDontUse()) {
      if (appState->textEditNodeId() == uniqueID()) {
        args = RenderTree::GenerateArgs::forTextEditing();
      }
    }

    if (!_scene->expandOnPageInstances()) {
      args.useSceneCursor = RenderTree::UseSceneCursor::YES;
      _scene->virtualNodesFillRenderingArgs(args);
    }

    _renderTree = generateRenderTree(self(), args);
    _hasRenderTree = true;

    // This result may also be able to be used for background blurs
    if (!_renderTree || _renderTree->canBeUsedInBackgroundBlurs()) {
      if (_hasRenderTreeWithoutBackgroundBlurs) {
        renderTreeWithoutBackgroundBlursForNode().erase(_fields.dkey());
      }
      _hasRenderTreeWithoutBackgroundBlurs = false;
    }
  }

  return _renderTree;
}

RenderTree::RenderNodePtr Node::renderTreeWithoutBackgroundBlurs() const {
  if (_hasRenderTree && (!_renderTree || _renderTree->canBeUsedInBackgroundBlurs())) {
    return _renderTree;
  }

  if (_hasRenderTreeWithoutBackgroundBlurs) {
    auto& map = renderTreeWithoutBackgroundBlursForNode();
    if (auto it = map.find(_fields.dkey()); it != map.end()) {
      return it->second;
    }
  } else if (isValid()) {
    bool isTextEdit = false;
    if (auto appState = getAppState_HackDontUse()) {
      if (appState->textEditNodeId() == uniqueID()) {
        isTextEdit = true;
      }
    }

    auto args = RenderTree::GenerateArgs::withoutBackgroundBlurs(
        RenderTree::EnforceExportBackgroundSettings::NO, RenderTree::ImageRenderingPolicy::DEFAULT,
        isTextEdit ? RenderTree::InteractiveState::TEXT_EDITING
                   : RenderTree::InteractiveState::NORMAL);

    if (!_scene->expandOnPageInstances()) {
      args.useSceneCursor = RenderTree::UseSceneCursor::YES;
      _scene->virtualNodesFillRenderingArgs(args);
    }

    auto tree = generateRenderTree(self(), args);
    if (tree) {
      renderTreeWithoutBackgroundBlursForNode().insert_or_assign(_fields.dkey(), tree);
    }
    _hasRenderTreeWithoutBackgroundBlurs = true;
    return tree;
  }

  return nullptr;
}

// Copy our cached data to caller's reference parameter.
// Return true if we had previously computed it.
bool Node::getCachedRenderTree(RenderTree::RenderNodePtr& result) const {
  result = _renderTree;
  return _hasRenderTree;
}

