void Node::incVisibleMaskChildCount(int delta) {
  REPORT_IF_FALSE_DEBUG_ONLY(isValid());

  auto& map = visibleMaskChildCountForNode();
  auto it = map.find(_fields.dkey());

  int visibleMaskChildCount = (it != map.end() ? it->second : 0) + delta;
  if (visibleMaskChildCount) {
    map.insert_or_assign(_fields.dkey(), visibleMaskChildCount);
  } else {
    map.erase(_fields.dkey());
  }

  _areChildSiblingMasksDirty = true;
}

bool Node::canHaveAnnotation() const {
  std::set<NodeType> annotatableNodeTypes = {NodeType::FRAME,
                                             NodeType::VECTOR,
                                             NodeType::STAR,
                                             NodeType::LINE,
                                             NodeType::ELLIPSE,
                                             NodeType::RECTANGLE,
                                             NodeType::REGULAR_POLYGON,
                                             NodeType::ROUNDED_RECTANGLE,
                                             NodeType::TEXT,
                                             NodeType::SYMBOL,
                                             NodeType::INSTANCE};

  // Ignore things in immutable frames because that's a signal it's from Figjam
  return annotatableNodeTypes.contains(type()) && !isInImmutableFrame() && !isGroup();
}

