  REPORT_IF_TRUE_DEBUG_ONLY(isValid());
}

Node::Node(SceneGraph* scene, GUID guid, NodeTracking tracking)
    : _scene(scene),
      _uniqueID(guid),
      _tracking(tracking),
      _repeatingContentStatus(RepeatingContentStatus::UNSET),
      _hasDirtyBounds(true),
      _isAbsoluteTextBoundsDirty(true),
      _hasRenderTree(false),
      _hasRenderTreeWithoutBackgroundBlurs(false),
      _isPartOfLoop(false),
      _cachedIsSymbolSublayer(false),
      _cachedIsInstanceSublayer(false),
      _cachedIsModuleSublayer(false),
      _cachedIsSlideSublayer(false),
      _cachedIsImmutableFrameSublayer(false),
      _cachedIsGhost(false),
      _cachedDescendsFromRoot(false),
      _cachedIsVisibleMask(false),
      _areChildSiblingMasksDirty(false) {
  // Enforce invariant that Node is always owned by a SceneGraph.
  REPORT_IF_FALSE_DEBUG_ONLY(scene);
  REPORT_IF_FALSE_DEBUG_ONLY(guid.isValid());
  REPORT_IF_FALSE_DEBUG_ONLY(_tracking == tracking);  // make sure we have enough bits

  // Ensure that these have been initialized to false (https://github.com/figma/figma/pull/9405)
  REPORT_IF_FALSE_DEBUG_ONLY(!_hasRenderTree && !_hasRenderTreeWithoutBackgroundBlurs);

  // These have historically been set on each node right after it was constructed. We used to use
  // "Node::set" for this but this was converted into raw property mutations because it's much
  // faster. The old code unnecessarily wasted 0.25 seconds during loading for our Enterprise.fig
  // benchmark document.
  _fields.setDefaultsForNode();

  REPORT_IF_FALSE_DEBUG_ONLY(isValid());

  if (_scene && _scene->hasViewerShim()) {
    DeprecatedJsSceneHooks::Node_created(
        getOrCreateWeakHandle(),
        SkewScene{_scene->getOrCreateViewerShim().rawJsValue.shallowClone()}, _uniqueID.sessionID,
        _uniqueID.localID);
  }
}

Node::~Node() {
  absoluteTextBoundsForNode().erase(_fields.dkey());

  if (_hasRenderTreeWithoutBackgroundBlurs) {
    renderTreeWithoutBackgroundBlursForNode().erase(_fields.dkey());
  }

  resolvedFillPaintDataForNode().erase(_fields.dkey());
  resolvedStrokePaintDataForNode().erase(_fields.dkey());
  resolvedEffectDataForNode().erase(_fields.dkey());
  resolvedLayoutGridDataForNode().erase(_fields.dkey());

  invalidateCachedTextSublayerCount();

  visibleSiblingMaskForNode().erase(_fields.dkey());
  visibleMaskChildCountForNode().erase(_fields.dkey());

  selfAndChildrenWithLayoutGridsForNode().erase(_fields.dkey());

  diagramChildrenForNode().erase(_fields.dkey());

  cachedBooleanOpForNode().erase(_fields.dkey());
  pathForSpreadForNode().erase(_fields.dkey());

  if (isValid()) {
    // This object must not outlive the scene from which it spawned, otherwise it will leak
    // because SceneGraph, as owner of all its nodes, should be the sole allocator and
    // deallocator.
    REPORT_IF_FALSE_DEBUG_ONLY(_scene && _scene->isAlive());
    // Must have unregistered node from owning SceneGraph before destruction,
    // otherwise SceneGraph has an invalid GUID key!
    if (NodePtr registered = _scene->nodeByIdOrNull(_uniqueID)) {
      if (registered == self()) {
        REPORT_ERROR("destructing node is still registered");
      }
    }
    if (_scene && _scene->hasViewerShim()) {
      DeprecatedJsSceneHooks::Node_destroyed(getOrCreateWeakHandle());
    }
  } else {
    REPORT_IF_TRUE_DEBUG_ONLY(_scene);
  }
}

